import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleCheck, faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons';

const ToDo = ({ toDo, markDone, setUpdateData, deleteTask, updateTask }) => {
  // State to manage the currently editing task ID, title, and details
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedDetails, setEditedDetails] = useState('');

  // Function to handle the editing of a task
  const handleEditTask = (taskId) => {
    if (!editedTitle.trim()) {
      alert("Task title cannot be empty!");
      return;
    }
    updateTask(taskId, { title: editedTitle, details: editedDetails });
    setEditingTaskId(null);
  };
  const handleDeleteTask = (id) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      deleteTask(id);
    }
  };
  
  // Then use it in your icon
  <span title="Delete" onClick={() => handleDeleteTask(task.id)}>
    <FontAwesomeIcon icon={faTrashCan} />
  </span>
  
  return (
    <>
      {toDo && toDo
        .sort((a, b) => a.id > b.id ? 1 : -1) // Sort tasks by ID
        .map((task, index) => {
          const createdAt = new Date(task.createdAt).toLocaleString(); // Format task creation date

          return (
            <React.Fragment key={task.id}>
              <div className="col taskBg">
                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span> {/* Display task number */}
                  {editingTaskId === task.id ? (
                    // If editing, show input for title
                    <input
                      type="text"
                      value={editedTitle}
                      onChange={(e) => setEditedTitle(e.target.value)}
                      placeholder="Edit task title..."
                      style={{ color: '#000', backgroundColor: '#fff', margin: '10px 0', width: '100%' }}
                    />
                  ) : (
                    // Otherwise, show the task title
                    <span className="taskText">{task.title}</span>
                  )}
                  <span className="taskTime" style={{ marginLeft: '10px' }}>{createdAt}</span> {/* Show task creation time */}
                  <span className={`taskPriority ${task.priority}`}>{task.priority}</span> {/* Show task priority */}
                </div>
                <div className="iconsWrap">
                  {/* Icon to mark task as done or not done */}
                  <span title="Completed / Not Completed" onClick={() => markDone(task.id)}>
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>
                  {/* Icon to trigger editing of the task */}
                  <span title="Edit" onClick={() => {
                    setEditingTaskId(task.id); // Set the task ID to be edited
                    setEditedTitle(task.title); // Populate the title input with current title
                    setEditedDetails(task.details || ''); // Populate the details textarea with current details
                  }}>
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                  {/* Icon to delete the task */}
                  <span title="Delete" onClick={() => deleteTask(task.id)}>
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>
                <div className="detailsSection" style={{ display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '10px' }}>
                  <p><strong>Details:</strong> {task.details || 'No details provided.'}</p> {/* Show task details */}
                  {editingTaskId === task.id && (
                    <>
                      {/* Textarea for editing task details */}
                      <textarea
                        value={editedDetails}
                        onChange={(e) => setEditedDetails(e.target.value)}
                        rows={3}
                        placeholder="Edit task details..."
                        style={{ color: '#000', backgroundColor: '#fff', margin: '10px 0', width: '100%', border: '1px solid #ccc', borderRadius: '4px' }}
                      />
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        {/* Button to save changes */}
                        <button
                          onClick={() => handleEditTask(task.id)}
                          style={{ backgroundColor: 'blue', color: 'white', padding: '8px 12px', border: 'none', borderRadius: '4px' }}
                        >
                          Save
                        </button>
                        {/* Button to cancel editing */}
                        <button
                          onClick={() => setEditingTaskId(null)}
                          style={{ backgroundColor: 'red', color: 'white', padding: '8px 12px', border: 'none', borderRadius: '4px' }}
                        >
                          Cancel
                        </button>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </React.Fragment>
          );
        })
      }
    </>
  );
}

export default ToDo;
