import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCircleCheck, faPen, faTrashCan
} from '@fortawesome/free-solid-svg-icons';

const ToDo = ({ toDo, markDone, setUpdateData, deleteTask, updateTask }) => {
  const [editingTaskId, setEditingTaskId] = useState(null); // State to track which task is being edited
  const [editedTitle, setEditedTitle] = useState(''); // State to hold edited title
  const [editedDetails, setEditedDetails] = useState(''); // State to hold edited details

  const handleEditTask = (taskId) => {
    // Call the function to update the task with the new title and details
    updateTask(taskId, { title: editedTitle, details: editedDetails });
    setEditingTaskId(null); // Reset editing state after saving
  };

  return (
    <>
      {toDo && toDo
        .sort((a, b) => a.id > b.id ? 1 : -1)
        .map((task, index) => {
          // Format the createdAt date
          const createdAt = new Date(task.createdAt).toLocaleString();

          return (
            <React.Fragment key={task.id}>
              <div className="col taskBg">
                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span>
                  {editingTaskId === task.id ? (
                    <input
                      type="text"
                      value={editedTitle}
                      onChange={(e) => setEditedTitle(e.target.value)}
                      placeholder="Edit task title..."
                    />
                  ) : (
                    <span className="taskText">{task.title}</span>
                  )}
                  <span className="taskTime">{createdAt}</span> {/* Displaying the created time */}
                  <span className={`taskPriority ${task.priority}`}>{task.priority}</span> {/* Displaying the priority */}
                </div>
                <div className="iconsWrap">
                  <span title="Completed / Not Completed"
                    onClick={() => markDone(task.id)}
                  >
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>

                  <span title="Edit"
                    onClick={() => {
                      setEditingTaskId(task.id);
                      setEditedTitle(task.title); // Set current title for editing
                      setEditedDetails(task.details || ''); // Set current details for editing
                    }}
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </span>

                  <span title="Delete"
                    onClick={() => deleteTask(task.id)}
                  >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>

                {/* Details Section */}
                {editingTaskId === task.id ? (
                  <div className="detailsSection">
                    <textarea
                      value={editedDetails}
                      onChange={(e) => setEditedDetails(e.target.value)}
                      rows={3}
                      placeholder="Edit task details..."
                    />
                    <div>
                      <button onClick={() => handleEditTask(task.id)}>Save</button>
                      <button onClick={() => setEditingTaskId(null)}>Cancel</button>
                    </div>
                  </div>
                ) : (
                  <div className="detailsSection">
                    <p><strong>Details:</strong> {task.details || 'No details provided.'}</p>
                    <button onClick={() => {
                      setEditingTaskId(task.id);
                      setEditedTitle(task.title); // Set current title for editing
                      setEditedDetails(task.details || ''); // Set current details for editing
                    }}>Edit Task</button>
                  </div>
                )}
              </div>
            </React.Fragment>
          );
        })
      }
    </>
  );
}

export default ToDo;
