import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCircleCheck, faPen, faTrashCan
} from '@fortawesome/free-solid-svg-icons';

const ToDo = ({ toDo, markDone, setUpdateData, deleteTask }) => {
  return (
    <>
      {toDo && toDo.length > 0 ? (
        toDo
          .sort((a, b) => a.id - b.id) // Sort tasks by their IDs in ascending order
          .map((task, index) => {
            // Format the createdAt timestamp into a readable string
            const createdAt = new Date(task.createdAt).toLocaleString();

            return (
              <div className="col taskBg" key={task.id}>
                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span>
                  <span className="taskText" style={{ cursor: 'pointer' }}>
                    {task.title}
                    <span className="taskDetails"> {/* Hoverable Details */}
                      <strong>Details:</strong> {task.details}
                    </span>
                  </span>
                  <span className="taskCreatedAt"> (Created: {createdAt})</span>
                  <span className="taskPriorityBox"> {/* Boxed Priority */}
                    (Priority: <span className="priorityText">{task.priority}</span>)
                  </span>
                </div>
                <div className="iconsWrap">
                  <span 
                    title="Completed / Not Completed"
                    onClick={() => markDone(task.id)}
                    aria-label={`Mark ${task.status ? 'not completed' : 'completed'}: ${task.title}`}
                  >
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>

                  {!task.status && (
                    <span 
                      title="Edit"
                      onClick={() => setUpdateData(task)}
                      aria-label={`Edit task: ${task.title}`}
                    >
                      <FontAwesomeIcon icon={faPen} />
                    </span>
                  )}

                  <span 
                    title="Delete"
                    onClick={() => deleteTask(task.id)}
                    aria-label={`Delete task: ${task.title}`}
                  >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>
              </div>
            );
          })
      ) : (
        <div>No tasks available.</div>
      )}
    </>
  );
}

export default ToDo;
