// AddTaskForm.jsx
import React from 'react';

const AddTaskForm = ({ newTask, setNewTask, addTask }) => {
  const [priority, setPriority] = React.useState('low'); // Default priority

  const handleAddTask = () => {
    addTask(priority); // Pass priority to addTask
    setPriority('low'); // Reset priority after adding
  };

  return (
    <div>
      <input 
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        className="form-control"
        placeholder="Add a new task"
      />
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </select>
      <button onClick={handleAddTask} className="btn btn-primary">Add Task</button>
    </div>
  );
};

export default AddTaskForm;
