import React, { useState } from 'react';
import { Box, Input, Select, Button, FormLabel } from '@chakra-ui/react';

const AddTaskForm = ({ newTask, setNewTask, addTask }) => {
  const [taskDetails, setTaskDetails] = useState(''); // State for task details
  const [priority, setPriority] = useState('low'); // Default priority
  const [deadline, setDeadline] = useState(''); // State for deadline

  const handleAddTask = () => {
    if (newTask.trim()) {
      addTask(priority, taskDetails, deadline); // Pass the selected priority, details, and deadline
      setNewTask(''); // Reset task name
      setTaskDetails(''); // Reset task details
      setPriority('low'); // Reset priority after adding
      setDeadline(''); // Reset deadline after adding
    }
  };

  return (
    <Box mb={3}>
      <FormLabel htmlFor="taskName">Task Name:</FormLabel>
      <Input 
        id="taskName"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a new task"
      />
      
      <FormLabel htmlFor="taskDetails" mt={2}>Task Details:</FormLabel>
      <Input 
        id="taskDetails"
        value={taskDetails}
        onChange={(e) => setTaskDetails(e.target.value)}
        placeholder="Add task details"
      />
      
      <FormLabel htmlFor="taskPriority" mt={2}>Priority:</FormLabel>
      <Select 
        id="taskPriority"
        value={priority} 
        onChange={(e) => setPriority(e.target.value)} 
      >
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </Select>
      
      <FormLabel htmlFor="taskDeadline" mt={2}>Deadline:</FormLabel>
      <Input 
        id="taskDeadline"
        type="date"
        value={deadline}
        onChange={(e) => setDeadline(e.target.value)}
      />
      
      <Button 
        onClick={handleAddTask} 
        colorScheme="blue"
        mt={3}
        isDisabled={!newTask.trim()} // Disable button if task name is empty
      >
        Add Task
      </Button>
    </Box>
  );
};

export default AddTaskForm;
