import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCircleCheck, faPen, faTrashCan
} from '@fortawesome/free-solid-svg-icons';

const ToDo = ({ toDo, markDone, setUpdateData, deleteTask }) => {
  const [activeTaskId, setActiveTaskId] = useState(null); // State to track the active task for details

  const toggleTaskDetails = (id) => {
    setActiveTaskId(activeTaskId === id ? null : id); // Toggle task details visibility
  };

  // Function to determine the style based on priority
  const getPriorityStyle = (priority) => {
    switch (priority) {
      case 'Low':
        return { backgroundColor: 'green', color: 'white' };
      case 'Medium':
        return { backgroundColor: 'yellow', color: 'black' };
      case 'High':
        return { backgroundColor: 'red', color: 'white' };
      default:
        return { backgroundColor: 'gray', color: 'white' }; // Default style if priority is unknown
    }
  };

  // Normalize the priority value to ensure consistent display
  const normalizePriority = (priority) => {
    if (priority) {
      return priority.charAt(0).toUpperCase() + priority.slice(1).toLowerCase(); // Capitalize first letter and lowercase the rest
    }
    return 'Unknown'; // Fallback if priority is undefined
  };

  return (
    <>
      {toDo && toDo.length > 0 ? (
        toDo
          .sort((a, b) => a.id - b.id) // Sort tasks by their IDs in ascending order
          .map((task, index) => {
            // Format the createdAt timestamp into a readable string
            const createdAt = new Date(task.createdAt).toLocaleString();

            return (
              <div className="col taskBg" key={task.id}>
                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span>
                  <span className="taskText" onClick={() => toggleTaskDetails(task.id)} style={{ cursor: 'pointer' }}>
                    {task.title}
                  </span>
                  <span className="taskCreatedAt"> (Created: {createdAt})</span>
                  <span className="taskPriorityBox" style={getPriorityStyle(normalizePriority(task.priority))}>
                    {/* Boxed Priority */}
                    (Priority: <span className="priorityText">{normalizePriority(task.priority)}</span>)
                  </span>
                </div>
                <div className="iconsWrap">
                  <span 
                    title="Completed / Not Completed"
                    onClick={() => markDone(task.id)}
                    aria-label={`Mark ${task.status ? 'not completed' : 'completed'}: ${task.title}`}
                  >
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>

                  {!task.status && (
                    <span 
                      title="Edit"
                      onClick={() => setUpdateData(task)}
                      aria-label={`Edit task: ${task.title}`}
                    >
                      <FontAwesomeIcon icon={faPen} />
                    </span>
                  )}

                  <span 
                    title="Delete"
                    onClick={() => deleteTask(task.id)}
                    aria-label={`Delete task: ${task.title}`}
                  >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>

                {/* Display task details if this task is active */}
                {activeTaskId === task.id && (
                  <div className="taskDetails">
                    <p><strong>Details:</strong> {task.details}</p>
                  </div>
                )}
              </div>
            );
          })
      ) : (
        <div>No tasks available.</div>
      )}
    </>
  );
}

export default ToDo;
