import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleCheck, faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons';

const ToDo = ({ toDo, markDone, setUpdateData, deleteTask }) => {
  const [hoveredTaskId, setHoveredTaskId] = useState(null); // State to track hovered task

  return (
    <>
      {toDo && toDo
        .sort((a, b) => a.id > b.id ? 1 : -1)
        .map((task, index) => {
          // Format the createdAt date
          const createdAt = new Date(task.createdAt).toLocaleString();

          return (
            <React.Fragment key={task.id}>
              <div 
                className="col taskBg"
                onMouseEnter={() => setHoveredTaskId(task.id)} // Set hover state on mouse enter
                onMouseLeave={() => setHoveredTaskId(null)} // Reset hover state on mouse leave
              >
                <div className={task.status ? 'done' : ''}>
                  <span className="taskNumber">{index + 1}</span>
                  <span className="taskText">{task.title}</span>
                  <span className="taskTime">{createdAt}</span> {/* Displaying the created time */}
                  {hoveredTaskId === task.id && ( // Conditionally render details
                    <div className="taskDetails">
                      <p><strong>Priority:</strong> {task.priority}</p>
                      <p><strong>Details:</strong> {task.details || 'No details provided.'}</p>
                    </div>
                  )}
                </div>
                <div className="iconsWrap">
                  <span title="Completed / Not Completed"
                    onClick={() => markDone(task.id)}
                  >
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>

                  {task.status ? null : (
                    <span title="Edit"
                      onClick={() => setUpdateData(task)}
                    >
                      <FontAwesomeIcon icon={faPen} />
                    </span>
                  )}

                  <span title="Delete"
                    onClick={() => deleteTask(task.id)}
                  >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>
              </div>
            </React.Fragment>
          );
        })
      }
    </>
  );
}

export default ToDo;
